package printstatement;

public class PrintingEx6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=10,b=5;
		
		System.out.println(a+"h");
		
		System.out.println(a+'h');
		
		System.out.println(a+"hii");
		
		System.out.println(a+"hii"+b);
		
		System.out.println(4+a+"hii"+b);
		
		System.out.println(4+a+"hii"+b+2);
		
		System.out.println(4+a+"hii"+(b+2));
		
		System.out.println(a+'a'+"hii"+'a');
		
		System.out.println(a-'a'+"hii"+'a'+a);
		
		System.out.println(a-4+"hii"+(a-4));
		
		System.out.println(a*4+"hii"+a*4);

	}

}
